CREATE OR REPLACE VIEW v_my_sessions AS SELECT sid,       serial#,       audsid,       username,       osuser,       TYPE,       machine,       action,       state,       status,       'kill_own_session(' || sid || ',' || serial# || ');' kill_script  FROM v$session WHERE (osuser, machine) IN (SELECT osuser, machine                               FROM v$session v                              WHERE v.audsid = userenv('SESSIONID')                                AND v.sid = userenv('SID'));